const SocialLink = ({ socialNetwork, url }) => {
  return (
    <a href={url} target="_blank" rel="noreferrer" className="social-media-link">
      {SocialMap[socialNetwork]}
    </a>
  );
};

const SocialLinkList = ({ socialLinks }) => {
  return (
    <div className="social-links">
      {socialLinks.map((link) => (
        <SocialLink key={link.id} {...link} />
      ))}
    </div>
  );
};

export default SocialLinkList;
